<?php// Inialize sessionsession_start();// Include database connection settingsinclude('config.inc');$host="localhost"; // Host name $username="naveen"; // Mysql username $password="mysql"; // Mysql password $db_name="project"; // Database name $tbl_name="members"; // Table name// Connect to server and select databse.mysql_connect("$host", "$username", "$password")or die("cannot connect"); mysql_select_db("$db_name")or die("cannot select DB");// username and password sent from form $myusername=$_POST['email_id']; $mypassword=$_POST['password'];// To protect MySQL injection (more detail about MySQL injection)$myusername = stripslashes($myusername);$mypassword = stripslashes($mypassword);$myusername = mysql_real_escape_string($myusername);$mypassword = mysql_real_escape_string($mypassword);$sql="SELECT * FROM $tbl_name WHERE email_id='$myusername' and password='$mypassword'";$result=mysql_query($sql);// Mysql_num_row is counting table row$count=mysql_num_rows($result);// If result matched $myusername and $mypassword, table row must be 1 rowif($count==1){// Register $myusername, $mypassword and redirect to file "login_success.php"//session_register("myusername");//session_register("mypassword"); //header("location:page1.php");// Set username session variable$_SESSION['email_id'] = $_POST['email_id'];// Jump to secured pageheader('Location: page1.php');}else {echo "Wrong Username or Password";// Jump to login pageheader('Location: index.php');}?>